import { useState } from 'react';
import './App.css';
import Box from './component/Box';
import { choice } from './utils/choice';
function App() {
  const [userSelect,setUserSelect]=useState(null)
  const [computerSelect,setComputerSelect]=useState(null)
  const [result,setResult]=useState("")
 const play=(userChoice)=>{
  setUserSelect(choice[userChoice])
let computerChoice=randomChoice()
setComputerSelect(computerChoice)
judgement(choice[userChoice],computerChoice)

 };
 const judgement=(user,computer)=>{
  let userChoice=user.name
  let computerChoice=computer.name
  userChoice
 }
const randomChoice=()=>{
  let itemArray = Object.keys(choice) //객체의 키값만 뽑아서 array 로 만들어주는 함수이다.

  let randomItem=Math.floor(Math.random()*itemArray.length);
  let final = itemArray[randomItem]

  return choice[final]
}

  return (
    <>
    <div className="main">
      <Box user={'You'} result={result} item={userSelect}/>
      <Box user={'Computer'} result={result} item={computerSelect}/>
    </div>
    <div className='Btns' >
      <button onClick={()=>play("scissor")}>가위</button>
      <button onClick={()=>play("rock")}>바위</button>
      <button onClick={()=>play("paper")}>보</button>
    </div>
    </>
  );
}

export default App;
